import "./SeasonDisplay.css";

import React from "react";

const getSeason = (lat, month) => {
  if (month < 3 && month > 8) return lat > 0 ? "Summer" : "Winter";
  else return lat > 0 ? "Winter" : "Summer";
};

const SeasonDisplay = (props) => {
  const Season = getSeason(props.lat, new Date().getDate());
  let advice =
    Season === "Winter"
      ? "It is so Chilly in Here "
      : "GO Out!A little bitt of Summer is What the whole year is all about";

  let icon = Season === "Winter" ? "snowflake" : "sun";
  return (
    <div className={`season-display ${season}`}>
      <i className={`icon-left massive ${icon} icon`} />
      <h1 className="advice">{advice}</h1>
      <i className={`icon-right massive  ${icon} icon`} />
    </div>
  );
};

export default SeasonDisplay;
