import "./SeasonDisplay.css";

import React from "react";

const seasonConfig = {
  Summer: {
    text: "GO Out!A little bitt of Summer is What the whole year is all about",
    iconName: "sun",
  },
  Winter: {
    text: "It is so chilly in here",
    iconName: "snowflake",
  },
};

const getSeason = (lat, month) => {
  if (month < 3 && month > 8) return lat > 0 ? "Summer" : "Winter";
  else return lat > 0 ? "Winter" : "Summer";
};

const SeasonDisplay = (props) => {
  const Season = getSeason(props.lat, new Date().getDate());
  console.log(Season);
  seasonConfig[Season];

  return <div></div>;
};

export default SeasonDisplay;
