import React from "react";

const getSeason = (lat, month) => {
  if (month < 3 && month > 8) return lat > 0 ? "summer" : "winter";
  else return lat > 0 ? "winter" : "summer";
};

const SeasonDisplay = (props) => {
  let advice;
  const Season = getSeason(props.lat, new Date().getDate());
  if (Season === "winter") {
    advice = "Have some warm cloths";
  } else {
    advice =
      "GO Out!A little bitt of Summer is What the whole year is all about";
  }
  return (
    <div>
      <h1>Latitude : {props.lat} </h1>
      <h1>longitude : {props.long}</h1>
      <br />
      <br />
      <hr />
      <h1>It is {Season} Time</h1>
      <br />
      {advice}
    </div>
  );
};

export default SeasonDisplay;
